import React from 'react'
import './signupMainbody.css'
import SignupHeader from './signupHeader'
import {Redirect} from 'react-router-dom'

import axios from 'axios';


export default class SignupMainbody extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            email: '',
            password: '',
            confirmPassword: '',
            phone: '',
            passwordError: ''

        }
    }


    handelUsernameChange = (event) => {
        this.setState({
            username: event.target.value
        })
    };

    handelEmailChange = (event) => {
        this.setState({
            email: event.target.value
        })
    };
    handelPasswordChange = (event) => {
        this.setState({
            password: event.target.value

        })
    };
    handelConfirmpasswordChange = (event) => {
        this.setState({
            confirmPassword: event.target.value
        })
    };

    handleMobileNumChange = (event) => {
        this.setState({
            phone: event.target.value
        })
    };









    handelSubmit = (event) => {
        event.preventDefault();
        console.log(this.state);

        if (this.state.password !== this.state.confirmPassword) {
            alert("Passwords do not match!");
        }
        else
            {

                axios
                    .post('http://192.168.43.128:8095/employee', this.state)
                    .then(response =>{
                        console.log(response)
                    }).catch(error =>{
                    console.log(error)
                })

                    // method: 'post',
                    // url: 'http://192.168.1.245:8095/employee',
                    // //http://192.168.1.245:8095/employee
                    // data: {
                    //     username:this.state.username,
                    //
                    //     password:this.state.password,
                    //     email:this.state.email,
                    //     phone:this.state.phone,
                    }


            }



    handleOnClick = () => {
        // some action...
        // then redirect
        const { name, value } = e.target;
        let formErrors = { ...this.state.formErrors };

        switch (name) {
            case "firstName":
                formErrors.firstName =
                    value.length < 3 ? "minimum 3 characaters required" : "";
                break;
            case "lastName":
                formErrors.lastName =
                    value.length < 3 ? "minimum 3 characaters required" : "";
                break;
            case "email":
                formErrors.email = emailRegex.test(value)
                    ? ""
                    : "invalid email address";
                break;
            case "password":
                formErrors.password =
                    value.length < 6 ? "minimum 6 characaters required" : "";
                break;
            default:
                break;
        }

        this.setState({redirect: true});
        alert("successfully registered")
    };


    render() {
        //const {username,email, password, confirmPassword, phone} =  this.state



            if (this.state.redirect) {
                return <Redirect push to="/login"/>;
            }

        return (
            <div>
                <SignupHeader/>
                <form onSubmit={this.handelSubmit}>
                    <h1 className='head'>ONE-ON-ONE</h1>
                    <div className="container">
                        <div className="card card-container">

                            <p id="profile-name" className="profile-name-card"/>
                            <form className="form-signin">
                                <h1 className="log">SIGN UP</h1>


                                <input type="text"
                                       id="inputUsername"
                                       className="form-control"
                                       placeholder="UserName"
                                       value={this.state.username}
                                       onChange={this.handelUsernameChange}
                                       required autoFocus/>


                                <input type="email"
                                       id="inputEmail"
                                       className="form-control"
                                       placeholder="Email"
                                       value={this.state.email}
                                       onChange={this.handelEmailChange}
                                       required/>


                                <input type="password"
                                       id="inputPassword"
                                       className="form-control"
                                       placeholder="Password"
                                       value={this.state.password}
                                       onChange={this.handelPasswordChange}
                                       required/>


                                <input type="password"
                                       id="inputConfirmPassword"
                                       className="form-control"
                                       placeholder="Confirm Password"
                                       value={this.state.confirmPassword}
                                       onChange={this.handelConfirmpasswordChange}
                                       required/>


                                <input type="number"
                                       id="inputMobileNum"
                                       className="form-control"
                                       placeholder="Phone"
                                       value={this.state.phone}
                                       onChange={this.handleMobileNumChange}

                                       required/>

                                <div>
                                    <br/>
                                    <button  className="btn btn-lg btn-primary btn-block btn-signin"
                                        onClick={this.handleOnClick}
                                             type="submit">
                                        Sign up
                                    </button>





                                </div>
                            </form>

                        </div>
                        {/* /card-container */}
                    </div>
                </form>
            </div>
        );
    }

}
