import React from 'react'
import {Form, TextArea} from "semantic-ui-react";

var bgColors = {
    "Default": "#81b71a",
    "Blue": "#00B1E1",
    "Cyan": "#",
    "Green": "#8CC152",
    "Red": "#E9573F",
    "Yellow": "#d3d3d3",
};


class Managermain5 extends React.Component {
    constructor(props) {
        super(props);
        this.state = { values: [] };
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    createUI(){
        return this.state.values.map((el, i) =>
            <div key={i}>
                <form>
                <input type="text"
                       id="question"
                       placeholder="1.  question ??" value={el||''} onChange={this.handleChange.bind(this, i)} />
                       <br/>

                <TextArea style={{backgroundColor: bgColors.Yellow}} placeholder='Answers'/>
                </form>


                <input type='button' value='remove' onClick={this.removeClick.bind(this, i)}/>
            </div>
        )
    }

    handleChange(i, event) {
        let values = [...this.state.values];
        values[i] = event.target.value;
        this.setState({ values });
    }

    addClick(){
        this.setState(prevState => ({ values: [...prevState.values, '']}))
    }

    removeClick(i){
        let values = [...this.state.values];
        values.splice(i,1);
        this.setState({ values });
    }

    handleSubmit(event) {
        alert('A name was submitted: ' + this.state.values.join(', '));
        event.preventDefault();
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                {this.createUI()}
                <input type='button' value='add more' onClick={this.addClick.bind(this)}/>
                <input type="submit" value="Submit" />
            </form>
        );
    }
}

export default Managermain5
