import React from 'react'

import { Form, TextArea,Responsive, Segment, Button } from 'semantic-ui-react'
import Managerheader1 from "../Managerpg1/Managerheader1";
import ManagerNav from "../Managerpg1/ManagerNav";
import ManagerFooter from "../Managerpg1/ManagerFooter";

var bgColors = {
    "Default": "#81b71a",
    "Blue": "#00B1E1",
    "Cyan": "#",
    "Green": "#8CC152",
    "Red": "#E9573F",
    "Yellow": "#d3d3d3",
};


class Managermain4 extends React.Component {
    constructor(props) {
        super(props);
        this.state = { values: [] };
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    createUI(){
        return this.state.values.map((el, i) =>
            <div key={i}>

                <Form>
                    <input type="text"
                           id="question"
                           placeholder="1.  question ??" value={el||''} onChange={this.handleChange.bind(this, i)} />
                    <br/>

                    <TextArea style={{backgroundColor: bgColors.Yellow}} placeholder='Answers'/>
                </Form>



            </div>
        )
    }

    handleChange(i, event) {
        let values = [...this.state.values];
        values[i] = event.target.value;
        this.setState({ values });
    }

    addClick(){
        this.setState(prevState => ({ values: [...prevState.values, '']}))
    }

    removeClick(i){
        let values = [...this.state.values];
        values.splice(i,1);
        this.setState({ values });
    }

    handleSubmit(event) {
        alert('A name was submitted: ' + this.state.values.join(', '));
        event.preventDefault();
    }

    render() {
        return (
            <div>
                <Managerheader1/>
                <ManagerNav/>

                <form onSubmit={this.handleSubmit}>
                    {this.createUI()}


                    <Form>

                        <input type="text"
                               id="question"
                               placeholder="1.  question ??"
                        />

                        <TextArea style={{backgroundColor: bgColors.Yellow}} placeholder='Answers'/>
                    </Form>



                    <div style={{ "width" :"200px"}} className="ui buttons">
                        <input className="ui green button"  type='button' value='Add More' onClick={this.addClick.bind(this)}/>
                        <input className="ui red button"  type='button' value='Remove' onClick={this.removeClick.bind(this)}/>
                    </div>
                    <div className='ui'>
                        <button className="ui green button">Save</button>
                    </div>
                </form>
                <br/>
                <br/>
                <br/>
                <br/>
                <br/>
                <br/>
                <ManagerFooter/>
            </div>
        );
    }
}

export default Managermain4
