import React from 'react'
import './signupMainbody.css'
import SignupHeader from './signupHeader'
import axios from "../loginpg/logMainbody";


export default class SignupMainbody extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            email: '',
            password: '',
            confirmPassword: '',
            mobileNum: '',
            passwordError: ''
        }
    }


    handleSubmit = e => {
        e.preventDefault();
        console.log(this.state)


        const response = axios({
            method: 'post',
            url: 'http://192.168.1.245:8095/employee',
            data: this.state
        });
    }



    handleUsernameChange = (event) => {
        this.setState({
            username: event.target.value
        })
    }

    handleEmailChange = (event) => {
        this.setState({
            email: event.target.value
        })
    }
    handlePasswordChange = (event) => {
        this.setState({
            password: event.target.value
        })
    }
    handleConfirmPasswordChange = (event) => {
        this.setState({
            confirmPassword: event.target.value
        })
    }
    handleMobileNumChange = (event) => {
        this.setState({
            mobileNum: event.target.value
        })
    }
    handleSubmit = (event) => {
        event.preventDefault();


    }





    render() {
        return (
            <div>
                <SignupHeader/>
                <form className="form-signin" onSubmit={this.handleSubmit}>
                    <div className="container">
                        <div className="card card-container">

                            <p id="profile-name" className="profile-name-card"/>

                                <h1 className="log">SIGN UP</h1>


                                <input type="text"
                                       id="inputUsername"
                                       className="form-control"
                                       placeholder="Username"
                                       value={this.state.username}
                                       onChange={this.handleUsernameChange}
                                       required autoFocus/>


                                <input type="email"
                                       id="inputEmail"
                                       className="form-control"
                                       placeholder="Email-ID"
                                       value={this.state.email}
                                       onChange={this.handleEmailChange}
                                       required autoFocus/>


                                <input type="password"
                                       id="inputPassword"
                                       className="form-control"
                                       placeholder="Password"
                                       value={this.state.password}
                                       onChange={this.handlePasswordChange}
                                       required/>


                                <input type="password"
                                       id="inputConfirmPassword"
                                       className="form-control"
                                       placeholder="Confirm Password"
                                       value={this.state.confirmPassword}
                                       onChange={this.handleConfirmPasswordChange}
                                       required/>
                                <div>

                                    {this.state.passwordError}

                                </div>

                                <input //type="number"
                                       id="inputMobileNum"
                                       className="form-control"
                                       placeholder="Mobile no."
                                       value={this.state.mobileNum}
                                       onChange={this.handleMobileNumChange}
                                       required/>


                                <div>
                                    <br/>
                                    <button  className="btn btn-lg btn-primary btn-block btn-signin" type="submit">Sign
                                        up
                                    </button>
                                </div>

                        </div>
                        {/* /card-container */}
                    </div>
                </form>
            </div>
        );
    }

}
