import React from 'react'
import './logMainbody.css'
import axios from 'axios';
import LogHeader from './logHeader'
import {BrowserRouter,Route,Link, Redirect} from "react-router-dom";


class LogMainbody extends React.Component{
    constructor(props) {
        super(props);
        this.state = { email: '' ,
            password: ''}

    }

    changeHandler = e => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }


    submitHandler = e => {
        e.preventDefault();
        console.log(this.state);

        axios
            .post('http://192.168.1.245:8095/login',this.state)

            .then(response =>{
                console.log(response)
            }).catch(error => {
            console.log(error)
            })
            };


    setRedirect=() =>
    {
        this.setState({
            redirect:true
        })

    };


    render() {
        const{email,password}=this.state;
        if (this.state.redirect) {
            return <Redirect to="/manager"/>;
        }
        return (

            <div className="form-signin">
                <LogHeader/>
                <form onSubmit={this.submitHandler}>
                    <h1 className='head'>ONE-ON-ONE</h1>
                    <div className="container">
                        <div className="card card-container">
                            <h1 className="log">LOGIN</h1>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                className="form-control"
                                placeholder="Email ID"
                                value={email} onChange={this.changeHandler}
                            />
                            <input
                                type="password"
                                id="password"
                                name="password"
                                className="form-control"
                                placeholder="Password"
                                value={password} onChange={this.changeHandler}
                            />
                            <button
                                className="btn btn-lg btn-primary btn-block btn-signin" onClick={this.setRedirect}
                            >Sign in</button>

                        </div>{/* /card-container */}

                    </div>
                </form>
            </div>

        );
    }




}
export default LogMainbody
