            //     email: "",
            //     password: "",
            //     confirmPassword: ""
            }

        this.handleChange = this.handleChange.bind(this);
        this.submitUserRegistrationForm = this.submitUserRegistrationForm.bind(this);


    };



    // handelUsernameChange = (event) => {
    //     this.setState({
    //         username: event.target.value
    //     })
