                                       id="inputMobileNum"
                                       className="form-control"
                                       placeholder="Phone"
                                       value={this.state.fields.phone}
                                       onChange={this.handleChange}
                                       //onChange={(e) => this.handleChange(e)}

                                       required/>
                                <div className="errorMsg">{this.state.errors.phone}</div>

                                    <br/>
