import React from 'react'
import './logMainbody.css'
import axios from 'axios';
import LogHeader from './logHeader'


class LogMainbody extends React.Component{
    constructor(props) {
        super(props);
        this.state = { email: '' ,
            password: ''};

    }

    changeHandler = e => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }


    submitHandler= e => {
        e.preventDefault();
        console.log(this.state)
        axios.post('http://192.168.1.245:8095/login', this.state)
            .then(response => {
            console.log(response)
        }).catch(error => {
            console.log(error)
        })
    }

    render(){
        return (<div>
            <LogHeader/>

            <form className="form-signin"  onSubmit={this.submitHandler}>

            <div className="container">
                <div className="card card-container">



                        <h1 className="log">LOGIN</h1>
                        <input type="email"
                               id="inputUsername"
                               className="form-control"
                               placeholder="Email"
                               onChange={this.changeHandler}


                               required autoFocus/>

                        <input type="password"
                               id="inputPassword"
                               className="form-control"
                               placeholder="Password"
                               onChange={this.changeHandler}
                               required />

                        <button className="btn btn-lg btn-primary btn-block btn-signin"
                                type="submit">Sign in</button>



                </div>{/* /card-container */}
            </div>
</form>
            </div>
    );
    }
}
export default LogMainbody
