import React from 'react'
import './signupMainbody.css'
import SignupHeader from './signupHeader'

import axios from 'axios';


const emailRegex = RegExp(
    /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

const formValid = ({ formErrors, ...rest }) => {
    let valid = true;

    // validate form errors being empty
    Object.values(formErrors).forEach(val => {
        val.length > 0 && (valid = false);
    });

    // validate the form was filled out
    Object.values(rest).forEach(val => {
        val === null && (valid = false);
    });

    return valid;
};


export default class SignupMainbody extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            email: '',
            password: '',
            confirmPassword: '',
            mobileNum: '',
            formErrors: {
                username: "",
                email: "",
                password: "",
                confirmPassword: "",
                mobileNum:""
            }
        };
    }


    /*handelUsernameChange = (event) => {
        this.setState({
            username: event.target.value
        })
    };

    handelEmailChange = (event) => {
        this.setState({
            email: event.target.value
        })
    };
    handelPasswordChange = (event) => {
        this.setState({
            password: event.target.value
        })
    };
    handelConfirmPasswordChange = (event) => {
        this.setState({
            confirmPassword: event.target.value
        })
    };

    handleMobileNumChange = (event) => {
        this.setState({
            mobileNum: event.target.value
        })
    }; */

    handelSubmit = (event) => {
        event.preventDefault();

        if (formValid(this.state)) {
            console.log(`
        --SUBMITTING--
        UserName: ${this.state.username}
        Email: ${this.state.email}
        Password: ${this.state.password}
        Confirm Password: ${this.state.confirmPassword}
        Mobile Number: ${this.state.mobileNum}
      `);
        } else {
            console.error("FORM INVALID - DISPLAY ERROR MESSAGE");
        }


        console.log(this.state);


        const response = axios({
            method: 'post',
            url: 'http://192.168.1.93:8095/adddata',
            //http://192.168.1.245:8095/employee
            data: this.state

        });
    };

    handleChange = e => {
        e.preventDefault();
        const { name, value } = e.target;
        let formErrors = { ...this.state.formErrors };

        switch (name) {
            case "username":
                formErrors.firstName =
                    value.length < 3 ? "minimum 3 characaters required" : "";
                break;
            case "email":
                formErrors.email = emailRegex.test(value)
                    ? ""
                    : "invalid email address";
                break;
            case "password":
                formErrors.password =
                    value.length < 6 ? "minimum 6 characaters required" : "";
                break;
            case "confirmPassword":
                formErrors.confirmPassword =
                    value.length < 6 ? "minimum 6 characaters required" : "";
                break;
            case "mobileNum":
                formErrors.mobileNum =
                    value.length <10 ? "Enter 10 digits" : "";
            default:
                break;
        }

        this.setState({ formErrors, [name]: value }, () => console.log(this.state));
    };



    render() {
        const {formErrors} = this.state;
        return (
            <div>
                <SignupHeader/>
                <div className="container">
                    /*<div className="card card-container">*/

                    <p id="profile-name" className="profile-name-card"/>

                    <h1 className="log">SIGN UP</h1>


                    <input type="text"
                           id="inputUsername"
                           className="form-control"
                        //className={formErrors.username.length > 0 ? "error" : null}
                           placeholder="UserName"
                           onChange={this.handleChange}
                           required autoFocus/>
                    <input type="email"
                           id="inputEmail"
                        //className={formErrors.email.length > 0 ? "error" : null}
                           className="form-control"
                           placeholder="Email"
                        //value={this.state.email}
                           onChange={this.handleChange}
                           required autoFocus/>

                    <input type="password"
                           id="inputPassword"
                           className="form-control"
                        //className={formErrors.password.length > 0 ? "error" : null}
                           placeholder="Password"
                        //value={this.state.password}
                           onChange={this.handleChange}
                           required/>

                    <input type="password"
                           id="inputConfirmPassword"
                           className="form-control"
                        //className={formErrors.confirmPassword.length > 0 ? "error" : null}
                           placeholder="Confirm Password"
                        //value={this.state.confirmPassword}
                           onChange={this.handleChange}
                           required/>

                    <input type="text"
                           id="inputMobileNum"
                           className="form-control"
                        //className={formErrors.mobileNum.length > 0 ? "error" : null}
                           placeholder="Phone"
                        // value={this.state.mobileNum}
                           onChange={this.handleChange}
                           required/>
                    <br/>

                    <div>
                        <button  className="btn btn-lg btn-primary btn-block btn-signin" type="submit">Sign
                            up
                        </button>
                    </div>


                </div>
            </div>

            </div>

        );
    }

}
