import React from 'react'
import './signupMainbody.css'
import SignupHeader from './signupHeader'


export default class SignupMainbody extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            username: '',
            email: '',
            password: '',
            confirmPassword: '',
            mobileNum: '',
            passwordError: ''
        }
    }


    handelUsernameChange = (event) => {
        this.setState({
            username: event.target.value
        })
    }

    handelEmailChange = (event) => {
        this.setState({
            email: event.target.value
        })
    }
    handelPasswordChange = (event) => {
        this.setState({
            password: event.target.value
        })
    }
    handelConfirmpasswordChange = (event) => {
        this.setState({
            confirmPassword: event.target.value
        })
    }
    handelMobilenumChange = (event) => {
        this.setState({
            mobileNum: event.target.value
        })
    }
    handelSubmit = (event) => {
        event.preventDefault();


    }





    render() {
        return (
            <div>
                <SignupHeader/>
                <form onSubmit={this.handelSubmit}>
                    <div className="container">
                        <div className="card card-container">

                            <p id="profile-name" className="profile-name-card"/>
                            <form className="form-signin">
                                <h1 className="log">SIGN UP</h1>


                                <input type="text"
                                       id="inputUsername"
                                       className="form-control"
                                       placeholder="Employee ID"
                                       value={this.state.username}
                                       onChange={this.handelUsernameChange}
                                       required autoFocus/>


                                <input type="email"
                                       id="inputEmail"
                                       className="form-control"
                                       placeholder="Email"
                                       value={this.state.email}
                                       onChange={this.handelEmailChange}
                                       required autoFocus/>


                                <input type="password"
                                       id="inputPassword"
                                       className="form-control"
                                       placeholder="Password"
                                       value={this.state.password}
                                       onChange={this.handelPasswordChange}
                                       required/>


                                <input type="password"
                                       id="inputConfirmPassword"
                                       className="form-control"
                                       placeholder="Confirm Password"
                                       value={this.state.confirmPassword}
                                       onChange={this.handelConfirmpasswordChange}
                                       required/>
                                <div>

                                    {this.state.passwordError}

                                </div>

                                <input type="number"
                                       id="inputMobileNum"
                                       className="form-control"
                                       placeholder="Mobile no."
                                       value={this.state.mobilenum}
                                       onChange={this.handelMobilenumChange}
                                       required/>


                                <div>
                                    <br/>
                                    <button  className="btn btn-lg btn-primary btn-block btn-signin" type="submit">Sign
                                        up
                                    </button>
                                </div>
                            </form>

                        </div>
                        {/* /card-container */}
                    </div>
                </form>
            </div>
        );
    }

}
