import React from 'react'
import './logMainbody.css'
import axios from 'axios';
import LogHeader from './logHeader'


class LogMainbody extends React.Component{
    constructor(props) {
        super(props);
        this.state = { email: '' ,
            password: ''};

    }

    changeHandler = e => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }


    submitHandler = e => {
        e.preventDefault();
        // console.log(this.state)
        //  axios.post('http://192.168.1.245:8095/login',this.state)
        //      .then(response=>{
        //      console.log(response)
        //      this.setState({email: response.data, password: response.data})
        //  }).catch (error => {
        //      console.log(error)
        //  })
        //var url = `http://192.168.1.245:8095/login`;
        fetch('http://192.168.1.245:8095/login' ,{
            method: 'POST',
            mode:'no-cors',
            body : JSON.stringify(this.state),
            headers: new Headers({
                contentType: 'application/json',
                contentEncoding: 'UTF-8',
                Accept: 'application/json'
            })

        })
            .then(function (response) {
                return response.json()})

            .then(res =>{
                console.log('Post request response data',res)

            })
            .catch(error => console.error('Error', error))


    }

    render(){
        return (<div>
            <LogHeader/>

            <form className="form-signin"  onSubmit={this.submitHandler}>

            <div className="container">
                <div className="card card-container">



                        <h1 className="log">LOGIN</h1>
                        <input type="email"
                               id="inputUsername"
                               className="form-control"
                               placeholder="Email"
                               onChange={this.changeHandler}


                               required autoFocus/>

                        <input type="password"
                               id="inputPassword"
                               className="form-control"
                               placeholder="Password"
                               onChange={this.changeHandler}
                               required />

                        <button className="btn btn-lg btn-primary btn-block btn-signin"
                                type="submit">Sign in</button>



                </div>{/* /card-container */}
            </div>
</form>
            </div>
    );
    }
}
export default LogMainbody
