import React from 'react'
import './signupMainbody.css'
import SignupHeader from './signupHeader'
import {Redirect} from 'react-router-dom'

import axios from 'axios';

const emailRegex = RegExp(
    /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);


const formValid = ({ formErrors, ...rest }) => {
    let valid = true;

    // validate form errors being empty
    Object.values(formErrors).forEach(val => {
        val.length > 0 && (valid = false);
    });

    // validate the form was filled out
    Object.values(rest).forEach(val => {
        val === null && (valid = false);
    });

    return valid;
};

export default class SignupMainbody extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            email: '',
            password: '',
            confirmPassword: '',
            phone: '',
            formErrors: {
                firstName: "",
                lastName: "",
                email: "",
                password: "",
                confirmPassword: ""
            }

        }
    }


    // handelUsernameChange = (event) => {
    //     this.setState({
    //         username: event.target.value
    //     })
    // };
    //
    // handelEmailChange = (event) => {
    //     this.setState({
    //         email: event.target.value
    //     })
    // };
    // handelPasswordChange = (event) => {
    //     this.setState({
    //         password: event.target.value
    //
    //     })
    // };
    // handelConfirmpasswordChange = (event) => {
    //     this.setState({
    //         confirmPassword: event.target.value
    //     })
    // };
    //
    // handleMobileNumChange = (event) => {
    //     this.setState({
    //         phone: event.target.value
    //     })
    // };


    handelSubmit = (event) => {
        event.preventDefault();
        console.log(this.state);

        if (this.state.password !== this.state.confirmPassword) {
            alert("Passwords do not match!");
        }
        else
            {

                axios
                    .post('http://192.168.43.128:8095/employee', this.state)
                    .then(response =>{
                        console.log(response)
                    }).catch(error =>{
                    console.log(error)
                })

                    // method: 'post',
                    // url: 'http://192.168.1.245:8095/employee',
                    // //http://192.168.1.245:8095/employee
                    // data: {
                    //     username:this.state.username,
                    //
                    //     password:this.state.password,
                    //     email:this.state.email,
                    //     phone:this.state.phone,
                    }


            }



    handleOnClick = e => {
        // some action...
        // then redirect
        const { name, value } = e.target;
        let formErrors = { ...this.state.formErrors };

        switch (name) {
            case "username":
                formErrors.username =
                    value.length < 3 ? "minimum 3 characaters required" : "";
                break;
            case "email":
                formErrors.email = emailRegex.test(value)
                    ? ""
                    : "invalid email address";
                break;
            case "password":
                formErrors.password =
                    value.length < 6 ? "minimum 6 characaters required" : "";
                break;

            case "confirmPassword":
                formErrors.confirmPassword =
                    value.length < 6 ? "minimum 6 characaters required" : "";
                break;

            case "phone":
                formErrors.phone =
                    value.length <= 10 ? "10 digits required" : "";
                break;
            default:
                break;
        }

        this.setState({redirect: true});
        alert("successfully registered")
    };


    render() {
        const {formErrors} =  this.state;



            if (this.state.redirect) {
                return <Redirect push to="/login"/>;
            }

        return (
            <div>
                <SignupHeader/>
                <form onSubmit={this.handelSubmit}>
                    <h1 className='head'>ONE-ON-ONE</h1>
                    <div className="container">
                        <div className="card card-container">

                            <p id="profile-name" className="profile-name-card"/>
                            <form className="form-signin">
                                <h1 className="log">SIGN UP</h1>

                                <div className="form-control">
                                <input
                                    //className="{formErrors.username.length > 0 ? "error" : null}"
                                    type="text"
                                       id="inputUsername"

                                       placeholder="UserName"
                                       value={this.state.username}
                                       onChange={this.handleOnClick}
                                       required autoFocus/>
                                    {formErrors.firstName.length > 0 && (
                                        <span className="errorMessage">{formErrors.username}</span>
                                    )}
                                </div>

                                <div>
                                <input type="email"
                                       id="inputEmail"
                                       className="form-control"
                                       placeholder="Email"
                                       value={this.state.email}
                                       onChange={this.handleOnClick}
                                       required/>

                                    {formErrors.email.length > 0 && (
                                        <span className="errorMessage">{formErrors.email}</span>
                                    )}

                                </div>

                                <div>
                                <input type="password"
                                       id="inputPassword"
                                       className="form-control"
                                       placeholder="Password"
                                       value={this.state.password}
                                       onChange={this.handleOnClick}
                                       required/>

                                    {formErrors.password.length > 0 && (
                                        <span className="errorMessage">{formErrors.password}</span>
                                    )}

                                </div>

                                <div>
                                <input type="password"
                                       id="inputConfirmPassword"
                                       className="form-control"
                                       placeholder="Confirm Password"
                                       value={this.state.confirmPassword}
                                       onChange={this.handleOnClick}
                                       required/>

                                    {formErrors.confirmPassword.length > 0 && (
                                        <span className="errorMessage">{formErrors.confirmPassword}</span>
                                    )}

                                </div>

                                <div>
                                <input type="number"
                                       id="inputMobileNum"
                                       className="form-control"
                                       placeholder="Phone"
                                       value={this.state.phone}
                                       onChange={this.handleOnClick}

                                       required/>

                                    {formErrors.phone.length > 0 && (
                                        <span className="errorMessage">{formErrors.phone}</span>
                                    )}

                                </div>
                                <div>
                                    <br/>
                                    <button  className="btn btn-lg btn-primary btn-block btn-signin"
                                        onClick={this.handleOnClick}
                                             type="submit">
                                        Sign up
                                    </button>





                                </div>
                            </form>

                        </div>
                        {/* /card-container */}
                    </div>
                </form>
            </div>
        );
    }

}
