export default class SignupMainbody extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            fields: {},
            errors: {}

            // username: '',
            // email: '',
            // password: '',
            // confirmPassword: '',
            // phone: '',
            // formErrors: {
            //     firstName: "",
            //     lastName: "",
            //     email: "",
            //     password: "",
            //     confirmPassword: ""
            }

        // this.handleChange = {this.handleChange.bind(this)};
        // this.submitUserRegistrationForm = {() => this.submitUserRegistrationForm.bind()};


    }



    // handelUsernameChange = (event) => {
    //     this.setState({
    //         username: event.target.value
    //     })
    // };
    //
    // handelEmailChange = (event) => {
    //     this.setState({
    //         email: event.target.value
    //     })
    // };
    // handelPasswordChange = (event) => {
    //     this.setState({
    //         password: event.target.value
    //
    //     })
    // };
    // handelConfirmpasswordChange = (event) => {
    //     this.setState({
    //         confirmPassword: event.target.value
    //     })
    // };
    //
    // handleMobileNumChange = (event) => {
    //     this.setState({
    //         phone: event.target.value
    //     })
    // };

    handleChange(e) {
        let fields = this.state.fields;
        fields[e.target.name] = e.target.value;
        this.setState({
            fields
        });

    }

    // handelSubmit = (event) => {
    //     event.preventDefault();
    //     console.log(this.state);
    //
    //     if (this.state.password !== this.state.confirmPassword) {
    //         alert("Passwords do not match!");
    //     }
    //     else
    //         {
    //
    //             axios
    //                 .post('http://192.168.43.128:8095/employee', this.state)
    //                 .then(response =>{
    //                     console.log(response.json())
    //                 }).catch(error =>{
    //                 console.log(error)
    //             })
    //
    //                 // method: 'post',
    //                 // url: 'http://192.168.1.245:8095/employee',
    //                 // //http://192.168.1.245:8095/employee
    //                 // data: {
    //                 //     username:this.state.username,
    //                 //
    //                 //     password:this.state.password,
    //                 //     email:this.state.email,
    //                 //     phone:this.state.phone,
    //                 }
    //
    //
    //         }

    submitUserRegistrationForm(e) {
        e.preventDefault();
        console.log(this.state)
        axios
                        .post('http://192.168.1.96:8095/employee', this.state)
                        .then(response =>{
                            console.log(response.json())
                        }).catch(error =>{
                        console.log(error)
                    });
        if (this.validateForm()) {
            let fields = {};
            fields["username"] = "";
            fields["email"] = "";
            fields["password"] = "";
            fields["confirmPassword"] = "";
            fields["phone"] = "";
            this.setState({fields:fields});
            alert("Form submitted");
        }

    }


    validateForm() {

        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        if (!fields["username"]) {
            formIsValid = false;
            errors["username"] = "*Please enter your username.";
        }

        if (typeof fields["username"] !== "undefined") {
            if (!fields["username"].match(/^[a-zA-Z ]*$/)) {
                formIsValid = false;
                errors["username"] = "*Please enter alphabet characters only.";
            }
        }

        if (!fields["email"]) {
            formIsValid = false;
            errors["email"] = "*Please enter your email-ID.";
        }

        if (typeof fields["email"] !== "undefined") {
            //regular expression for email validation
            var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
            if (!pattern.test(fields["email"])) {
                formIsValid = false;
                errors["email"] = "*Please enter valid email-ID.";
            }
        }


        if (!fields["password"]) {
            formIsValid = false;
            errors["password"] = "*Please enter your password.";
        }

        if (typeof fields["password"] !== "undefined") {
            if (!fields["password"].match(/^.*(?=.{8,})(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%&]).*$/)) {
                formIsValid = false;
                errors["password"] = "*Please enter secure and strong password.";
            }
        }

        if (!fields["confirmPassword"]) {
            formIsValid = false;
            errors["password"] = "*Please enter your password.";
        }

        if (typeof fields["confirmPassword"] !== "undefined") {
            if (!fields["confirmPassword"].match(/^.*(?=.{8,})(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%&]).*$/)) {
                formIsValid = false;
                errors["confirmPassword"] = "*Please enter secure and strong password.";
            }
        }

        if (!fields["phone"]) {
            formIsValid = false;
            errors["phone"] = "*Please enter your mobile no.";
        }

        if (typeof fields["phone"] !== "undefined") {
            if (!fields["phone"].match(/^[0-9]{10}$/)) {
                formIsValid = false;
                errors["phone"] = "*Please enter valid mobile no.";
            }
        }

        this.setState({
            errors: errors
        });
        return formIsValid;


    }





    render() {
        const {formErrors} =  this.state;



            if (this.state.redirect) {
                return <Redirect push to="/login"/>;
            }

        return (
            <div>
                <SignupHeader/>
                <form onSubmit= {this.submitUserRegistrationForm}>
                    <h1 className='head'>ONE-ON-ONE</h1>
                    <div className="container">
                        <div className="card card-container">

                            <p id="profile-name" className="profile-name-card"/>
                            <form className="form-signin">
                                <h1 className="log">SIGN UP</h1>

                                <div >
                                <input
                                    //className="{formErrors.username.length > 0 ? "error" : null}"
                                    type="text"
                                    id="inputUsername"
                                    className="form-control"
                                    placeholder="UserName"
                                    value={this.state.fields.username}
                                    onChange={this.handleChange}
                                    //{this.state.errors.username}
                                       required autoFocus/>

                                </div>

                                <div>
                                <input type="email"
                                       id="inputEmail"
                                       className="form-control"
                                       placeholder="Email"
                                       value={this.state.fields.email}
                                       onChange={this.handleChange}
                                       required/>

                                    {/*{formErrors.email.length > 0 && (*/}
                                    {/*    <span className="errorMessage">{formErrors.email}</span>*/}
                                    {/*)}*/}

                                </div>

                                <div>
                                <input type="password"
                                       id="inputPassword"
                                       className="form-control"
                                       placeholder="Password"
                                       value={this.state.password}
                                       onChange={this.handleChange}
                                       required/>

                                    {/*{formErrors.password.length > 0 && (*/}
                                    {/*    <span className="errorMessage">{formErrors.password}</span>*/}
                                    {/*)}*/}

                                </div>

                                <div>
                                <input type="password"
                                       id="inputConfirmPassword"
                                       className="form-control"
                                       placeholder="Confirm Password"
                                       value={this.state.confirmPassword}
                                       onChange={this.handleChange}
                                       required/>

                                    {/*{formErrors.confirmPassword.length > 0 && (*/}
                                    {/*    <span className="errorMessage">{formErrors.confirmPassword}</span>*/}
                                    {/*)}*/}

                                </div>

                                <div>
                                <input type="text"
                                       pattern="[0-9]*"
                                       inputMode="numeric"
                                       id="inputMobileNum"
                                       className="form-control"
                                       placeholder="Phone"
                                       value={this.state.phone}
                                       onChange={this.handleChange}

                                       required/>

                                    {/*{formErrors.phone.length > 0 && (*/}
                                    {/*    <span className="errorMessage">{formErrors.phone}</span>*/}
                                    {/*)}*/}

                                </div>
                                <div>
                                    <br/>
                                    <button  className="btn btn-lg btn-primary btn-block btn-signin"
                                        onClick={this.handleChange}
                                             type="submit">
                                        Sign up
                                    </button>





                                </div>
                            </form>

                        </div>
                        {/* /card-container */}
                    </div>
                </form>
            </div>
        );
    }

}
