import React from 'react'
import './logMainbody.css'
import axios from 'axios';
import LogHeader from './logHeader'


class LogMainbody extends React.Component{
    constructor(props) {
        super(props);
        this.state = { email: '' ,
            password: ''};

    }

    changeHandler = e => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }


    submitHandler = e => {
        e.preventDefault();
        // console.log(this.state)
        //  axios.post('http://192.168.1.245:8095/login',this.state)
        //      .then(response=>{
        //      console.log(response)
        //      this.setState({email: response.data, password: response.data})
        //  }).catch (error => {
        //      console.log(error)
        //  })
        //var url = `http://192.168.1.245:8095/login`;
        fetch('http://192.168.1.245:8095/login' ,{
            method: 'POST',
            mode:'no-cors',
            body : JSON.stringify(this.state),
            headers: new Headers({
                contentType: 'application/json',
                contentEncoding: 'UTF-8',
                Accept: 'application/json'
            })

        })
            .then(function (response) {
                console.log(response)})

            .then(res =>{
                console.log('Post request response data',res)

            })
            .catch(error => console.error('Error', error))


    }



    render() {
        const{email,password}=this.state
        return (

            <div className="form-signin">
                <LogHeader/>
                <form  method ='POST' onSubmit={this.submitHandler}>
                    <h1 className='head'>ONE-ON-ONE</h1>
                    <div className="container">
                        <div className="card card-container">
                            <h1 className="log">LOGIN</h1>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                className="form-control"
                                placeholder="Email ID"
                                value={email} onChange={this.changeHandler}
                            />
                            <input
                                type="password"
                                id="password"
                                name="password"
                                className="form-control"
                                placeholder="Password"
                                value={password} onChange={this.changeHandler}
                            />
                            <button
                                className="btn btn-lg btn-primary btn-block btn-signin"
                            >Sign in</button>

                        </div>{/* /card-container */}

                    </div>
                </form>
            </div>

        );
    }




}
export default LogMainbody
